    vertical-align: middle;
    display: inline-block;
    object-fit: cover;
  }
  button {
    color: inherit;
  }

  li {
    list-style-type: none;
  }
  a {
    text-decoration: none;
    color: inherit;
    outline: none;
    cursor: pointer;

    // &:focus {
    //   outline: 0;
    // }
  }
  address {
    font-style: inherit;
  }
}

@mixin body {
  position: relative;
