//==========================media==========================//
@mixin from($breakpoint: $globalBP) {
  @media screen and (min-width: $breakpoint) {
    @content;
  }
}

@mixin to($breakpoint: $globalBP) {
  @media screen and (max-width: $breakpoint - 1) {
    @content;
  }
}
@mixin fromto($from: $globalBP, $to: $globalBP) {
  @media (min-width: $from) and (max-width: $to) {
    @content;
  }
}
//------------------text color----------------------------------
@mixin t-($color, $hover: false) {
  @if $hover {
    &:hover {
      color: map-get($colors, $color);
      color: Var(#{$color});
    }
  } @else {
    color: map-get($colors, $color);
    color: Var(#{$color});
  }
}

// .t {
//     @include t-(primary, true);
// }
// output-------
// .t:hover {
//   color: var(--primary);
// }

//------------------backgroundcolor----------------------------------*/
@mixin bg-($color, $opacity: false, $hover: false) {
  @if $color == 0 {
    background-color: transparent;
  } @else {
    @if $opacity {
      background-color: rgba(Color($colors, $opacity));
    } @else {
      background-color: map-get($colors, $color);
      background-color: Var(#{$color});
    }
  }

  @if $hover {
    &:hover {
      @include bg-($hover);
    }
  }
  // @if $text {
  //   @include t-($text);
  // }
}

//   .bg {
//         @include bg-(0, true);
//     }
// output-------
//   .bg {
//   background-color: transparent;
//   }

//------------------full size----------------------------------*/

@mixin full-($type) {
  @if $type == v {
    width: 100vw;
    min-width: 100vw;
    height: 100vh;
    min-height: 100vh;
  } @else if $type == vw {
    width: 100vw;
    min-width: 100vw;
  } @else if $type == vh {
    height: 100vh;
    min-height: 100vh;
  } @else if $type == h {
    height: 100%;
    min-height: 100%;
