//------------------prop----------------------------------*/
@mixin prop-size($size, $colors...) {
  &#{$size} {
    // @each $key, $value in $colors {
    //   #{$key}: $value;
    // //   @include prop(#{$key} $value);
    // }
    @include prop($colors...);
  }
}

@mixin prop($colors...) {
  @content;
  @each $key, $value in $colors {
    @if $key == bg {
      @include bg-($value);
    }
    // ----------------
    @else if $key == t {
      @include t-($value);
    }
    // -------------------------------
    @else if $key == m {
      @include m-($value);
    }
    // ----------------
    @else if $key == mr {
      @include mr-($value);
    }
    // ----------------
    @else if $key == ml {
      @include ml-($value);
    }
    // ----------------
    @else if $key == mt {
      @include mt-($value);
    }
    // ----------------
    @else if $key == mb {
      @include mb-($value);
    }
    // -------------------------------
    @else if $key == p {
      @include p-($value);
    }
    // ----------------
    @else if $key == pr {
      @include pr-($value);
    }
    // ----------------
    @else if $key == pl {
      @include pl-($value);
    }
    // ----------------
    @else if $key == pt {
      @include pt-($value);
    }
    // ----------------
    @else if $key == pb {
      @include pb-($value);
    }
    // ----------------
    @else {
      #{$key}: $value;
    }
  }
}

