//-------------------grid----------------------------------//
.grid {
  @include breakpointMaker {
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    grid-auto-flow: dense;
    // & > * {
    //   height: auto;
    // }
    &.stretch > * {
      height: min-content;
    }
  }
}
// ==================grid row================
.grid-row {
  --row-size: 1fr;
  display: grid;
  grid-template-rows: repeat(1, 1fr);

  @each $key, $value in $numbers {
    &-#{$key} {
      @extend .grid-row;
      grid-template-rows: repeat($key, 1fr);
    }
  }
}

// grid childs ----------------------
.item {
  @include breakpointMaker {
    // &-left {
    //   justify-self: start;
    //   align-self: start;
    // }
    // &-center {
    //   justify-self: center;
    //   align-self: center;
    // }
    // &-right {
    //   justify-self: end;
    //   align-self: end;
    // }
    &-full {
      grid-column: 1 / column-line-end;
      grid-row: 1 / row-line-end;
    }
    &-full-col {
      grid-column: 1 / column-line-end;
    }
    &-full-row {
      grid-row: 1 / row-line-end;
    }

    // number of columns taken per child item
    @each $key, $value in $numbers {
      &-#{$key} {
        grid-column-end: span $key;
      }
    }
  }
}
// grid & column gap-----------------------------
