%width-100 {
  width: 100%;
}
%margin-a {
  margin-left: auto;
  margin-right: auto;
}

%content {
  @each $key, $value in $breakpoints {
    &-#{$key} {
      @extend %width-100;
    }
    &-#{$key}-a {
      @extend %margin-a;
    }
  }

  @include from($sm) {
    @include prop-size("", max-width 540px);
    @include prop-size(-xl, max-width 960px);
    @include prop-size(-lg, max-width 720px);
    @include prop-size(-md, max-width 320px);
    @include prop-size(-sm, max-width 320px);
    @include prop-size(-movile, max-width 320px);
  }
  @include from($md) {
    @include prop-size("", max-width 720px);
    @include prop-size(-xl, max-width 1024px);
    @include prop-size(-lg, max-width 960px);
    @include prop-size(-md, max-width 540px);
    @include prop-size(-sm, max-width 460px);
  }
  @include from($lg) {
    @include prop-size("", max-width 960px);
    @include prop-size(-xl, max-width 1224px);
    @include prop-size(-lg, max-width 1024px);
    @include prop-size(-md, max-width 720px);
    // @include prop-size(-sm, max-width 1224px);
  }
  @include from($xl) {
    @include prop-size("", max-width 1024px);
    @include prop-size(-xl, max-width 1460px);
    @include prop-size(-lg, max-width 1224px);
    @include prop-size(-md, max-width 1460px);
    // @include prop-size(-sm, max-width 1460px);
  }
}

.container,
.content {
  @extend %content;
  @extend %width-100;
  $breakpoints: (sm, md, lg, xl, movile);

  @each $key in $breakpoints {
    &-#{$key} {
      @extend %width-100;
      @extend %content-#{$key};
    }
    &-#{$key}-m,
    &-#{$key}-a {
      @extend %margin-a;
      @extend %content-#{$key};
    }
  }

  &-m,
  &-a {
    @extend %width-100;
    @extend %content;
    @extend %margin-a;
    @extend %content;
  }
}
//-----------------------------------------------------//

// .ratio2 {
//   position: relative;
//   width: 100%;
//   height: 0;
// }

.ratio {
  @each $key, $value in $aspect-ratio {
    $name: #{$value}-#{$key};
    &-#{$name} {
      position: relative;
      width: 100%;
      height: 0;
      @include ratio($value, $key);
      // @extend .ratio2;
    }
  }
}
.video-container {
  // @extend .ratio-16-9;
  position: relative;
  width: 100%;
  height: 0;
  @include ratio(16, 9);
}

//==========================directions==========================//

.b,
.block {
  &-left {
    display: block;
    margin-right: auto;
  }
  &-right {
    display: block;
    margin-left: auto;
  }
  &-center {
    display: block;
    margin: auto;
  }
}

//-------------------section padding----------------------------------//
.section {
  &-sm {
    @include py-(20px);
    @include from($md) {
      @include py-(60px);
    }
  }
  &,
  &-md {
    @include py-(40px);
    @include from($md) {
      @include py-(90px);
    }
  }
  &-lg {
    @include py-(60px);
    @include from($md) {
      @include py-(150px);
    }
  }
  &-xl {
    @include py-(90px);
    @include from($md) {
      @include py-(200px);
    }
  }
}

