// @import "utilities/shadow";
// @import "utilities/shapes";
// @import "utilities/transform";
// @import "utilities/width-height";
// @import "utilities/background-colors";
// @import "utilities/typography";
// @import "utilities/alignment";

// ------------------------
// @import "debug";

body {
  margin: 0;
  background-color: rgb(177, 177, 177);
  font-family: Oswald, sans-serif;
  overflow: hidden;
}

.top-bar {
  padding-left: 15px;
  height: 50px;
  width: 100%;
  position: fixed;
  background-color: grey;
  display: flex;
  justify-content: center;
}

/* Font Awesome Icons */
.fas,
.far {
  font-size: 25px;
  position: relative;
  top: 5px;
  background-color: rgb(82, 82, 82);
  color: white;
  padding: 5px;
  border-radius: 5px;
  width: 40px;
  text-align: center;
  cursor: pointer;
}

.fas:hover,
.far:hover {
  filter: brightness(90%);
}

.fa-fill-drip {
  cursor: default;
}

.fa-undo-alt:hover,
.fa-trash-alt:hover {
  color: rgb(223, 37, 37);
}

/* Tools */
.active-tool {
  position: absolute;
  top: 9px;
  left: 5px;
}

.active-tool > span {
  background-color: rgb(82, 82, 82);
  border-radius: 5px;
  padding: 2.5px 16px;
  color: white;
  font-size: 20px;
  user-select: none;
}

.tool {
  position: relative;
  top: 3px;
}

.tool:not(:first-child) {
  margin-left: 8px;
}

.brush {
  background-color: rgb(114, 114, 114);
  height: 44px;
  width: 345px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.brush > * {
  margin-left: 10px;
}

.size {
  min-width: 40px;
  height: 35px;
  position: relative;
  top: 4px;
  left: -5px;
  background-color: rgb(82, 82, 82);
  border-radius: 5px;
  padding: 2.5px 8px;
  color: white;
  font-size: 20px;
  user-select: none;
}

/* Custom Slider */
.slider {
  -webkit-appearance: none;
  position: relative;
  left: -8px;
  width: 100px;
  height: 10px;
  background: rgb(177, 177, 177);
  outline: none;
  opacity: 0.7;
  cursor: pointer;
  -webkit-transition: 0.2s;
  transition: opacity 0.2s;
}

.slider:hover {
  opacity: 1;
}

.slider::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 10px;
  height: 10px;
  background: rgb(47, 47, 47);
}

.slider::-moz-range-thumb {
  width: 10px;
  height: 10px;
  background: rgb(47, 47, 47);
}

/* JS Color */
.jscolor {
  border-style: solid;
  border-radius: 5px;
  height: 35px;
  width: 120px;
  margin-left: 3px;
  text-align: center;
  cursor: pointer;
  outline: none;
}

/* Canvas */
canvas#canvas {
  // background-color: rgb(177, 177, 177);
  position: absolute;
  top: 50px;
  cursor: crosshair;
  z-index: 10;
}

/* Mobile Message */
.mobile-message {
  display: none;
}

/* Media Query: Mobile Devices */
@media screen and (max-width: 800px) {
  .mobile-message {
    position: absolute;
    height: 100vh;
    width: 100%;
    background-color: rgb(0, 0, 0);
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    z-index: 100;
  }
}

input {
  display: inline;
  // flex: 0 1;
}
