._resize {
  resize: both;
  overflow: auto;
}
._resize-h {
  resize: horizontal;
  overflow: auto;
}
._resize-v {
  resize: vertical;
  overflow: auto;
}

html {
  overflow-y: unset;
}

.page-bg {
  position: fixed;
  width: 100%;
  height: 100vh;
  background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url(/d7c9e49745f77bf3143a196cc69c294f.jpg);
  background-size: cover;
}

.mouse-dot, .mouse-circle {
  position: fixed;
  border-radius: 50%;
  z-index: 300;
  transform: translate(-50%, -50%);
  opacity: 0;
  pointer-events: none;
}

.mouse-circle {
  width: 6rem;
  height: 6rem;
  border: 0.1rem solid #c7a622;
  animation: mouseCircleAdim 10s infinite linear;
  transition: width 0.5s, height 0.5s, top 0.5s, left 0.5s, transform 0.5s, border-radius 0.5s;
  will-change: width, height, top, left, transform, border-radius;
}
.mouse-dot {
  width: 0.5rem;
  height: 0.5rem;
  background-color: #9b0e0e;
  animation: mouseDotAnim 10s infinite linear;
}

@keyframes mouseDotAnim {
  0% {
    width: 0.5rem;
    height: 0.5rem;
  }
  50% {
    width: 1.5rem;
    height: 1.5rem;
  }
  100% {
    width: 0.5rem;
    height: 0.5rem;
  }
}
@keyframes mouseCircleAdim {
  0% {
    width: 6rem;
    height: 6rem;
  }
  25% {
    width: 8rem;
    height: 8rem;
  }
  25% {
    width: 4rem;
    height: 4rem;
  }
  70% {
    width: 8rem;
    height: 8rem;
  }
  70% {
    width: 6rem;
    height: 6rem;
  }
}
.center, .section-1 {
  display: flex;
  justify-content: center;
  align-items: center;
}

.section-1 {
  position: relative;
  width: 100;
  height: 100vh;
}

.logo {
  position: absolute;
  top: 2rem;
  left: 2rem;
  font-size: 2rem;
  text-transform: uppercase;
  color: #fff;
}

.main-circle {
  width: 55rem;
  height: 55rem;
  position: absolute;
  overflow: hidden;
  border-radius: 50%;
  transition: width 1s, height 1s;
}
.main-circle:hover {
  width: 75rem;
  height: 75rem;
}
.main-circle img {
  mix-blend-mode: color-dodge;
  position: relative;
  transition: left 2s, top 2s;
  top: 0;
  left: 0;
  object-fit: cover;
  width: 100%;
}

.circles {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 1fr);
}

.circle {
  width: 22rem;
  height: 22rem;
  border-radius: 50%;
  background: red;
  align-self: center;
  justify-self: center;
  opacity: 0.5;
  position: relative;
  transition: left 2s, top 2s;
  top: 0;
  left: 0;
}
.circle-1 {
  background: url(/3de13fe1435cb2450fa28f24b433ed1a.jpg);
  background-size: cover;
  border: 0.5rem dotted #a14b1a;
  background-clip: content-box;
  margin-left: -10rem;
}
.circle-2 {
  background: url(/e57f67f3afacb18ef28bb06875c7bdab.jpg);
  background-size: cover;
  border: 0.5rem dashed #a14b1a;
  background-clip: content-box;
  margin-right: -10rem;
}
.circle-3 {
  background: url(/b830e82acd2ed0a3a235e4333cb666ee.jpg);
  background-size: cover;
  border: 1rem double #9b0e0e;
  background-clip: content-box;
  margin-left: -10rem;
}
.circle-4 {
  background: url(/d161d2fa774bedb681fbd3ca8af20204.jpg);
  background-size: cover;
  border: 0.7rem double #fff;
  background-clip: content-box;
  margin-right: -10rem;
}

.feature-text {
  font-size: 2rem;
  text-transform: uppercase;
  width: 0;
  word-wrap: break-word;
  line-height: 3.4;
  color: #fff;
  position: sticky;
  top: 0;
}
.feature-text-1 {
  margin: 0 auto 0 10rem;
}
.feature-text-2 {
  margin: 0 10rem 0 auto;
}

.main-btn {
  position: absolute;
  width: 13rem;
  height: 13rem;
  background: #9b0e0e;
  border-radius: 50%;
  bottom: 3rem;
  border: 0.2rem dotted #fff;
  padding: 0.1rem;
  overflow: hidden;
  z-index: 0;
}
.main-btn:hover .main-btn-content {
  transform: rotate(60deg);
  border: 0.2rem dotted #9b0e0e;
  transition: transform 0.2s 0.5s, border, 0;
}
.main-btn-content {
  border-radius: 50%;
  width: 100%;
  height: 100%;
  border: 0.2rem dotted #e7be08;
  transform: rotate(40deg);
  transition: transform 0.2s, border, 0.2s;
  z-index: 10;
}
.main-btn-line {
  transition: transform 0.4s;
  position: absolute;
  top: 4rem;
  width: 3rem;
  height: 0.5rem;
  border: 0.1rem solid #fff;
}
.main-btn-line:nth-child(1) {
  transform: rotateZ(-45deg) translateX(-0.65rem);
  transform-origin: left center;
  border-right: none;
}
.main-btn-line:nth-child(2) {
  transform: rotateZ(45deg) translateX(0.65rem);
  transform-origin: right center;
  border-left: none;
}
.main-btn-line:nth-child(3) {
  transform: rotateZ(90deg) translateX(0.5rem);
  border-left: none;
}
.main-btn span {
  position: absolute;
  bottom: 3rem;
  color: #fff;
  font-size: 1.1rem;
  font-weight: 600;
  text-transform: uppercase;
}

.ripple {
  position: absolute;
  width: 0;
  height: 0;
  background: #e7be08;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  animation: rippleAnim 0.5s forwards;
  z-index: -1;
}

@keyframes rippleAnim {
  0% {
    width: 0;
    height: 0;
  }
  100% {
    width: 250%;
    height: 250%;
  }
}
.section-heading {
  font-size: 2rem;
  font-weight: 300;
  color: #fff;
  text-transform: uppercase;
  position: absolute;
  top: 50%;
  left: 10rem;
  transform: translateY(-50%);
  width: 0;
  word-break: break-all;
  line-height: 4;
}

.section-2 {
  position: relative;
  padding: 10rem 10rem 15rem 10rem;
}
.section-2 .main-btn {
  left: 50%;
  bottom: -5rem;
  transform: translateX(-50%);
}

.about-me-text {
  width: 80%;
  margin: auto;
}
.about-me-text span {
  font-family: "Poppins", sans-serif;
  font-size: 15rem;
  font-weight: bold;
  color: #aaa;
  letter-spacing: 0.5rem;
  line-height: 0.9;
  text-shadow: 0 0 1rem #9b0e0e;
  position: relative;
  mix-blend-mode: color-dodge;
}

@keyframes aboutMeTextAnim {
  0% {
    color: #aaa;
    top: 0;
  }
  10%, 90% {
    color: #fff;
    top: 2rem;
  }
  100% {
    color: #aaa;
    top: 0;
  }
}
.section-3 {
  width: 100%;
  height: 100%;
  padding: 10rem 0;
  margin-top: 10rem;
  position: relative;
}

.project {
  width: 40rem;
  height: 45rem;
  overflow: hidden;
  margin: 5rem;
  background: #000;
  display: flex;
  position: relative;
  justify-content: center;
  border: 0.2rem dotted #9b0e0e;
  border-radius: 0.5rem;
  cursor: pointer;
  perspective: 20rem;
  transition: opacity 0.5s;
}
.projects {
  display: flex;
  flex-wrap: wrap;
  padding-bottom: 15rem;
}
.project img {
  width: 90%;
  object-fit: cover;
  position: absolute;
  top: 2rem;
  transition: top 4s, opacity 0.2s;
  opacity: 0.4;
  pointer-events: none;
}
.project:nth-child(odd):hover img {
  animation: oddImgAnim 4s 0.2s;
  opacity: 1;
}
.project:nth-child(even):hover img {
  animation: oddImgAnim 4s 0.2s;
  opacity: 1;
}
.project-img {
  animation: imgAnim 1s 1s both;
  transform-origin: top center;
  width: 100%;
  border-radius: 0.5rem;
  padding: 7rem 20rem;
  opacity: 0.9;
  display: block;
}

@keyframes imgAnim {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}
@keyframes oddImgAnim {
  0% {
    transform: rotateY(0);
  }
  25% {
    transform: rotateY(-2deg) rotate(2deg);
  }
  50% {
    transform: rotateY(2deg) rotate(-2deg);
  }
  100% {
    transform: rotateY(0);
  }
}
@keyframes evenImgAnim {
  0% {
    transform: rotateY(0);
  }
  25% {
    transform: rotateY(2deg) rotate(-2deg);
  }
  50% {
    transform: rotateY(-2deg) rotate(2deg);
  }
  100% {
    transform: rotateY(0);
  }
}
.project-img-wrapper {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 0;
  background: rgba(0, 0, 0, 0.9);
  animation: imgWrapperAnim 1s forwards;
  overflow: hidden scroll;
  z-index: 100;
  scroll-behavior: smooth;
}

@keyframes imgWrapperAnim {
  0% {
    height: 0;
  }
  100% {
    height: 100%;
  }
}
.project-hide-btn {
  position: fixed;
  top: 4rem;
  right: 4rem;
  background: transparent;
  border: none;
  font-size: 2rem;
  letter-spacing: 0.1rem;
  z-index: 200;
  transform: scale(0);
  visibility: hidden;
  transition: transform 0.5s, visibility 1s;
}
.project-hide-btn:hover {
  color: #fff;
}
.project-hide-btn.change {
  visibility: visible;
  transform: scale(1);
}

.projects-btn {
  left: 50%;
  transform: translateX(-50%);
}

.change > .main-btn-line:nth-child(1) {
  transform: rotateZ(45deg) translateX(-0.65rem);
}

.change > .main-btn-line:nth-child(2) {
  transform: rotateZ(-45deg) translateX(0.65rem);
}

.change > .main-btn-line:nth-child(3) {
  transform: rotateZ(90deg) translateX(-0.5rem);
  border-left: 0.1rem solid #fff;
  border-right: none;
}

.section-4 {
  width: 100%;
  position: relative;
}

.services-wrapper {
  width: 100%;
  flex-direction: column;
}

.service {
  width: 70%;
  margin: 2rem 0;
  border-bottom: 0.5rem solid #9b0e0e;
}
.service-btn {
  width: 100%;
  position: relative;
  display: block;
  height: 15rem;
}
.service-btn span {
  font-family: "Poppins";
  font-size: 10rem;
  font-style: italic;
  font-weight: bold;
  color: #9b0e0e;
  position: absolute;
  right: 0;
  transition: right 0.5s;
  pointer-events: none;
}
.service-text {
  font-size: 5rem;
  color: #777;
  letter-spacing: 0.1rem;
  height: 0;
  visibility: hidden;
  opacity: 0;
  transition: height 0.2s, opacity 0.5s 0.5s;
}
.service-text.change {
  transition: height 0.2s, opacity 0.5s;
  opacity: 1;
  height: 15rem;
  visibility: visible;
}

.section-5 {
  position: relative;
  height: 100vh;
  flex-direction: column;
}

.form-heading {
  font-family: "Poppins";
  font-size: 6rem;
  font-weight: 400;
  color: #9b0e0e;
  letter-spacing: 0.3rem;
  margin-bottom: 3rem;
  transition: opacity 0.3s;
}

.contact-form {
  flex-direction: column;
}
.contact-form-input {
  width: 60rem;
  padding: 0.5rem;
  margin: 1rem;
  background: rgba(255, 255, 255, 0.1);
  border: 0.1rem solid #9b0e0e;
  font-size: 1.7rem;
  font-weight: bold;
  color: #aaa;
  letter-spacing: 0.1rem;
  height: 5rem;
}
textarea.contact-form-input {
  height: 20rem;
  resize: none;
}

.form-submit-btn {
  width: 20rem;
  height: 5rem;
  background-color: #9b0e0e;
  font-size: 1.8rem;
  color: #fff;
}

.slideshow {
  display: flex;
  width: 50rem;
  margin-top: 10rem;
  padding: 0 2rem;
}
.slideshow-link {
  width: 20%;
  filter: brightness(0.5);
  transition: filter 0.5s;
}
.slideshow-link img {
  padding: 0 1rem;
}
.slideshow-link:nth-child(1), .slideshow-link:last-child {
  transition: width 0.5s, opacity 0.5s;
}

.fade-out {
  opacity: 0;
  width: 0;
}

.light {
  filter: brightness(1.5);
}

.navbar {
  position: fixed;
  top: 2.5rem;
  width: 100%;
  display: flex;
  justify-content: flex-end;
  padding-right: 3rem;
  transition: opacity 0.5s, visibility 0.5s, transform 0.5s;
  z-index: 100;
}
.navbar-link {
  font-size: 1.5rem;
  font-weight: 700;
  color: #fff;
  text-transform: uppercase;
  margin: 0 2.5rem;
  z-index: 100;
  text-shadow: 0 0.3rem 0.5rem #000;
}

.hide-navbar {
  opacity: 0;
  visibility: hidden;
  transform: translateX(10rem);
}

.menu-icon {
  position: fixed;
  top: 3.5rem;
  right: 0;
  width: 12rem;
  height: 7rem;
  flex-direction: column;
  opacity: 0;
  visibility: hidden;
  z-index: 101;
  transition: opacity 0.5s, visibility 0.5s;
  transform: translate(-50%, -50%);
  cursor: pointer;
}
.menu-icon-line {
  width: 4rem;
  height: 0.1rem;
  background: #fff;
  margin: 0.5rem 0;
  box-shadow: 0 0.3rem 0.5rem #000;
}

.show-menu-icon {
  opacity: 1;
  visibility: visible;
}

.progress-bar {
  position: fixed;
  background: #fff;
  width: 4rem;
  height: 4rem;
  bottom: 8rem;
  right: 8rem;
  border-radius: 50%;
  z-index: 200;
  overflow: hidden;
  transform: translate(-50%, -50%);
}
.progress-bar-circle {
  border-radius: 50%;
  width: 7.8rem;
  height: 7.8rem;
  background: #000;
  color: #fff;
  font-size: 4rem;
  z-index: 200;
  transition: transform 0.5s;
  pointer-events: none;
}

.half-circle,
.half-circle-top {
  position: absolute;
  top: 0;
  left: 0;
  width: 55%;
  height: 105%;
  transform-origin: right center;
  pointer-events: none;
}

.half-circle:nth-child(1) {
  background: #9b0e0e;
}

.half-circle:nth-child(2) {
  background: #9b0e0e;
}

.half-circle-top {
  background: #fff;
}

