//------------------text color----------------------------------*/
@mixin t-($color, $hover: false) {
  @if $hover {
    &:hover {
      color: map-get($colors, $color);
      color: Var(#{$color});
    }
  } @else {
    color: map-get($colors, $color);
    color: Var(#{$color});
