//------------------backgroundcolor----------------------------------*/
@mixin bg-($color, $hover: false, $text: none) {
  @if $color == 0 {
    background-color: transparent;
  } @else {
    background-color: map-get($colors, $color);
    background-color: Var(#{$color});
  }

  @if $hover {
    &:hover {
      @include bg-($hover);
      //   background-color: $hover;
    }
  }
  @if $text != none {
    @include t-($text);
  }
