    height: 100vh;
    min-height: 100vh;
  } @else if $type == vw {
    width: 100vw;
    min-width: 100vw;
  } @else if $type == vh {
    height: 100vh;
    min-height: 100vh;
  } @else if $type == h {
    height: 100%;
    min-height: 100%;
  } @else if $type == w {
    width: 100%;
    min-width: 100%;
  } @else if $type == a {
    width: auto;
    height: auto;
  } @else {
    width: 100%;
    height: 100%;
  }
}

