@mixin erasebreakpoints($property, $value) {
  $nombre: str-replace(&, ".");
  $nombre: str-replace($nombre, "#");
  $breakpoints: (
    xl: $xl,
    lg: $lg,
    md: $md,
    sm: $sm,
  );
  @each $key, $point in $breakpoints {
    @at-root .#{$key}\:#{$nombre} {
      @include to($point) {
        #{$property}: $value;
      }
    }
  }
