@mixin breakpointMakerTo($breakpointMaker: $breakpointMaker) {
  @content;
  @if $breakpointMaker {
    $howmany: length($breakpoints);
    $nombre: erase(separateTwoSelectors(1), ".");
    $nombre: erase($nombre, "#");

    $names: str-split(&, ", ");

    @if $howmany > 0 {
      @for $i from 1 through $howmany {
        $keys: index-to-key($breakpoints, $i);
        @include to(map-get($breakpoints, $keys)) {
          @at-root .#{$keys}\:#{$nombre} {
            // @at-root .#{$keys}\>#{$nombre} {
            @content;
          }
          @if length($names) > 1 {
            @for $i from 2 through length($names) {
              $algo: nth($names, $i);
              $algo: erase($algo, " ");
              $algo: erase($algo, ".");
              $algo: erase($algo, "#");
              $identifier: #{$keys}\: #{$nombre};

              @at-root .#{$keys}\:#{$algo} {
                @content;
              }
            }
          }
        }
      }
    }
  }
}

//------------------cal----------------------------------*/
@mixin clamp($min, $width, $max) {
  // width: clamp(min, prefered, max);
  min-width: $min;
  width: $width;
  max-width: $max;
}
//------------------box em----------------------------------*/
@mixin box-em($height, $width: $height, $lower: false) {
  height: $height;
  width: $width;
  @if $lower {
    line-height: $height - 0.2em;
  } @else {
    line-height: $height;
  }
  text-align: center;
}
//------------------box px----------------------------------*/

@mixin box-px($height, $font, $width: false) {
  font-size: $font;

  $height: strip-unit($height);
  $font: strip-unit($font);
  // $realHeight: ($height * 1em)/$font;

  $realHeight: ruleofthree($font, 1em, $height);

  height: $realHeight;
  line-height: $realHeight;
  @if $width {
    $width: strip-unit($width);
    $realWidth: math.div(($width * 1em), $font);
    width: $realWidth;
  }
}

// usage
// .box-em {
//   @include box(40px, 10px, 400px); // height, font, width
// }

// font-size: 10px;
// height: 4em;
// line-height: 4em;
// width: 40em;

//------------------prop----------------------------------*/
@mixin prop-size($size, $colors...) {
  &#{$size} {
    // @each $key, $value in $colors {
    //   #{$key}: $value;
    // //   @include prop(#{$key} $value);
    // }
    @include prop($colors...);
  }
}

@mixin prop($colors...) {
  @content;
  @each $key, $value in $colors {
    @if $key == bg {
      @include bg-($value);
    }
    // ----------------
    @else if $key == t {
      @include t-($value);
    }
    // -------------------------------
    @else if $key == m {
      @include m-($value);
    }
    // ----------------
    @else if $key == mr {
      @include mr-($value);
    }
    // ----------------
    @else if $key == ml {
      @include ml-($value);
    }
    // ----------------
    @else if $key == mt {
      @include mt-($value);
    }
    // ----------------
    @else if $key == mb {
      @include mb-($value);
    }
    // -------------------------------
    @else if $key == p {
      @include p-($value);
    }
    // ----------------
    @else if $key == pr {
      @include pr-($value);
    }
    // ----------------
    @else if $key == pl {
      @include pl-($value);
    }
    // ----------------
    @else if $key == pt {
      @include pt-($value);
    }
    // ----------------
    @else if $key == pb {
      @include pb-($value);
    }
    // ----------------
    @else {
      #{$key}: $value;
    }
  }
}

