//-------------------dark mode----------------------------------//
//  some colors don´t change
$colors-darkmode: (
  primary: $primary,
  secondary: $secondary,
  tertiary: $tertiary,
  quaternary: $quaternary,
  //  -----
    success: #28a745,
  info: #17a2b8,
  warning: #ffc107,
  danger: #dc3545,
);
$colorStatic-darkmode: (
  // light: $gray-dark,
    // dark: $gray-light,
    title: $gray-light,
  link: #17a2b8,
  // ----
    gray-dark: $gray-light,
  gray: $gray,
  gray-light: $gray-dark,
  white: $black,
  black: $white
);
// $notifiers-darkmode: (
//   success: #28a745,
//   info: #17a2b8,
//   warning: #ffc107,
//   danger: #dc3545,
// );

// body {
//   will-change: background-color;
//   transition: background-color 1s ease-in-out;
// }
body.dark-theme {
  background-color: $dark;
  background-color: var(--dark);
  // colors static
  @each $key, $value in $colorStatic-darkmode {
    --#{"" +$key}: #{$value};
  }
  // colors ----------------
  @each $key, $value in $colors-darkmode {
    --#{"" +$key}: #{$value};
  }
  // darken
  @each $key, $value in $colors-darkmode {
    // $algo: darken($value, $darken);
    --#{"" +$key}-dark: #{darken($value, $darken)};
  }
  // lighten
  @each $key, $value in $colors-darkmode {
    // $algo: lighten($value, $lighten);
    --#{"" +$key}-light: #{lighten($value, $lighten)};
  }

  // // notifiers
  // @each $key, $value in $notifiers-darkmode {
  //   --#{"" +$key}: #{$value};
  // }

  // // darken
  // @each $key, $value in $notifiers-darkmode {
  //   // $algo: darken($value, $darken);
  //   --#{"" +$key}-dark: #{darken($value, $darken)};
  // }
  // // lighten
  // @each $key, $value in $notifiers-darkmode {
  //   // $algo: lighten($value, $lighten);
  //   --#{"" +$key}-light: #{lighten($value, $lighten)};
  // }
  & img:not(.dark-theme) {
    opacity: 0.75;
    transition: opacity 0.5s ease-in-out;
    &:hover {
      opacity: 1;
    }
  }
}

