  }
}
// -----------------------------
._reveal-center {
  // background: red;
  // height: 500px;
  // width: 500px;
  position: relative;
  &::before {
    content: "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 50%;
    height: 50%;
    transform: translate(1px, 1px);
    border-bottom: 1px dashed blue;
    border-right: 1px dashed blue;
    z-index: $z-modal_1000 + 1;
  }
  &::after {
    content: "";
    display: block;
    position: absolute;
    bottom: 0;
    right: 0;
    width: 50%;
    height: 50%;
    // transform: translate(-1px, -1px);
    border-top: 1px dashed blue;
    border-left: 1px dashed blue;
    z-index: $z-modal_1000;
  }
}
//
// put it in body tag
._content {
  position: relative;
  &::after {
    border: red 2px dotted;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    // background-color: red;
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    // max-width: 1024px;
    // margin: auto;
    z-index: $z-modal_1000;
    @include from($sm) {
      max-width: 540px;
    }
    @include from($md) {
      max-width: 720px;
    }
    @include from($lg) {
      max-width: 960px;
    }
    @include from($xl) {
      max-width: 1024px;
    }
  }
}

//----------------------------------------------------*/
._screen_size {
  // position: relative;
  &::before {
    content: "tinny";
    position: fixed;
    top: 0;
    left: 0;
    background-color: $gray-dark;
    color: $white;
    z-index: 1000;
    padding: 3px 5px;
    border: $white 1px dotted;
    opacity: 0.7;

    $indes: $z-modal_1000 + 2;

    // @each $key, $value in $breakpoints {
    //   @include from($value) {
    //     content: "+#{$value}";
    //     font-size: ($font-size * $indes) / 2;
    //   }
    //   $indes: $indes + 1;
    // }

    @for $value from 1 to 30 {
      @include from($value * 50px) {
        content: "#{$value * 50}px";
        // font-size: ($font-size * $indes) / 9;
      }
      $indes: $indes + 1;
    }
  }
}
// resize
._resize {
  resize: both;
  overflow: auto;
  &-h {
    resize: horizontal;
    overflow: auto;
  }
  &-v {
    resize: vertical;
    overflow: auto;
  }
}

