@use "sass:math";
// @import "defaultVariables.scss";

// //==========================custome variables==========================*/
@import "variables.scss";
// //==========================================================*/
@import "0_reset/minireset.scss";
@import "settings";
// @import "layout";
// @import "components";
// @import "utilities";
@import "debug";
// //==========================================================*/
html {
  overflow-y: unset;
}
.container {
}

.page-bg {
  position: fixed;
  width: 100%;
  height: 100vh;
  // background: #000;
  background: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
    url(../images/bg.jpg);
  background-size: cover;
}

%circleDot {
  position: fixed;
  border-radius: 50%;
  z-index: 300;
  transform: translate(-50%, -50%);
  opacity: 0;
  pointer-events: none;
}
.mouse {
  &-circle {
    width: 6rem;
    height: 6rem;
    border: 0.1rem solid #c7a622;
    @extend %circleDot;
    animation: mouseCircleAdim 10s infinite linear;
    transition: width 0.5s, height 0.5s, top 0.5s, left 0.5s, transform 0.5s,
      border-radius 0.5s;
    will-change: width, height, top, left, transform, border-radius;
  }
  &-dot {
    width: 0.5rem;
    height: 0.5rem;
    background-color: #9b0e0e;
    @extend %circleDot;
    animation: mouseDotAnim 10s infinite linear;
  }
}
@keyframes mouseDotAnim {
  0% {
    width: 0.5rem;
    height: 0.5rem;
  }
  50% {
    width: 1.5rem;
    height: 1.5rem;
  }
  100% {
    width: 0.5rem;
    height: 0.5rem;
  }
}
@keyframes mouseCircleAdim {
  0% {
    width: 6rem;
    height: 6rem;
  }
  25% {
    width: 8rem;
    height: 8rem;
  }
  25% {
    width: 4rem;
    height: 4rem;
  }
  70% {
    width: 8rem;
    height: 8rem;
  }
  70% {
    width: 6rem;
    height: 6rem;
  }
}

// section
.center {
  display: flex;
  justify-content: center;
  align-items: center;
}
.section-1 {
  position: relative;
  width: 100;
  height: 100vh;
  @extend .center;
}

//
.logo {
  position: absolute;
  top: 2rem;
  left: 2rem;
  font-size: 2rem;
  text-transform: uppercase;
  color: #fff;
}

//
.main-circle {
  width: 55rem;
  height: 55rem;
  position: absolute;
  overflow: hidden;
  border-radius: 50%;
  transition: width 1s, height 1s;
  &:hover {
    width: 75rem;
    height: 75rem;
  }
  & img {
    mix-blend-mode: color-dodge;
    position: relative;
    transition: left 2s, top 2s;
    top: 0;
    left: 0;
    object-fit: cover;
    width: 100%;
    // border: 20px solid red;
  }
}

//
.circles {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, 1fr);
}
.circle {
  width: 22rem;
  height: 22rem;
  border-radius: 50%;
  background: red;
  align-self: center;
  justify-self: center;
  opacity: 0.5;
  position: relative;
  transition: left 2s, top 2s;
  top: 0;
  left: 0;

  &-1 {
    background: url(../images/landing/circle-1-img.jpg);
    background-size: cover;
    border: 0.5rem dotted #a14b1a;
    background-clip: content-box;
    margin-left: -10rem;
  }
  &-2 {
    background: url(../images/landing/circle-2-img.jpg);
    background-size: cover;
    border: 0.5rem dashed #a14b1a;
    background-clip: content-box;
    margin-right: -10rem;
  }
  &-3 {
    background: url(../images/landing/circle-3-img.jpg);
    background-size: cover;
    border: 1rem double #9b0e0e;
    background-clip: content-box;
    margin-left: -10rem;
  }
  &-4 {
    background: url(../images/landing/circle-4-img.jpg);
    background-size: cover;
    border: 0.7rem double #fff;
    background-clip: content-box;
    margin-right: -10rem;
  }
}

// text
.feature-text {
  font-size: 2rem;
  text-transform: uppercase;
  width: 0;
  word-wrap: break-word;
  line-height: 3.4;
  color: #fff;
  position: sticky;
  top: 0;
  &-1 {
    margin: 0 auto 0 10rem;
  }
  &-2 {
    margin: 0 10rem 0 auto;
  }
}

// ----------
.main {
  &-btn {
    position: absolute;
    width: 13rem;
    height: 13rem;
    background: #9b0e0e;
    border-radius: 50%;
    bottom: 3rem;
    border: 0.2rem dotted #fff;
    padding: 0.1rem;
    overflow: hidden;
    z-index: 0;
    &:hover &-content {
      transform: rotate(60deg);
      border: 0.2rem dotted #9b0e0e;
      transition: transform 0.2s 0.5s, border, 0;
    }
    &-content {
      border-radius: 50%;
      width: 100%;
      height: 100%;
      border: 0.2rem dotted #e7be08;
      transform: rotate(40deg);
      transition: transform 0.2s, border, 0.2s;
      z-index: 10;
    }
    &-line {
      transition: transform 0.4s;
      position: absolute;
      top: 4rem;
      width: 3rem;
      height: 0.5rem;
      border: 0.1rem solid #fff;
      &:nth-child(1) {
        transform: rotateZ(-45deg) translateX(-0.65rem);
        transform-origin: left center;
        border-right: none;
      }
      &:nth-child(2) {
        transform: rotateZ(45deg) translateX(0.65rem);
        transform-origin: right center;
        border-left: none;
      }
      &:nth-child(3) {
        transform: rotateZ(90deg) translateX(0.5rem);
        border-left: none;
      }
      // @at-root .change > &:nth-child(3) {
      //   transform: rotateZ(-45deg) translateX(-0.65rem);
      // }
      // @at-root .change > &:nth-child(3) {
      //   transform: rotateZ(45deg) translateX(-0.65rem);
      // }
      // @at-root .change > &:nth-child(3) {
      //   transform: rotateZ(90deg) translateX(-0.5rem);
      //   border-left: 0.1rem solid #fff;
      //   border-right: none;
      // }
    }
    & span {
      position: absolute;
      bottom: 3rem;
      color: #fff;
      font-size: 1.1rem;
      font-weight: 600;
      text-transform: uppercase;
    }
  }
}

.ripple {
  position: absolute;
  width: 0;
  height: 0;
  background: #e7be08;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  animation: rippleAnim 0.5s forwards;
  z-index: -1;
}

@keyframes rippleAnim {
  0% {
    width: 0;
    height: 0;
  }
  100% {
    width: 250%;
    height: 250%;
  }
}

// heading --------------------------------------
.section {
  &-heading {
    font-size: 2rem;
    font-weight: 300;
    color: #fff;
    text-transform: uppercase;
    position: absolute;
    top: 50%;
    left: 10rem;
    transform: translateY(-50%);
    width: 0;
    word-break: break-all;
    line-height: 4;
  }
}
// section 2 --------------------------------------
.section-2 {
  position: relative;
  padding: 10rem 10rem 15rem 10rem;

  & .main-btn {
    left: 50%;
    bottom: -5rem;
    transform: translateX(-50%);
  }
}

.about-me-text {
  width: 80%;
  margin: auto;
  & span {
    font-family: $font2, sans-serif;
    font-size: 15rem;
    font-weight: bold;
    color: #aaa;
    letter-spacing: 0.5rem;
    line-height: 0.9;
    text-shadow: 0 0 1rem #9b0e0e;
    position: relative;
    mix-blend-mode: color-dodge;
    // animation: aboutMeTextAnim ;
  }
}

@keyframes aboutMeTextAnim {
  0% {
    color: #aaa;
    top: 0;
  }
  10%,
  90% {
    color: #fff;
    top: 2rem;
  }

  100% {
    color: #aaa;
    top: 0;
  }
}

// ---------------------
.section-3 {
  width: 100%;
  height: 100%;
  padding: 10rem 0;
  margin-top: 10rem;
  position: relative;
}
.project {
  &s {
    display: flex;
    flex-wrap: wrap;
    padding-bottom: 15rem;
  }
  width: 40rem;
  height: 45rem;
  overflow: hidden;
  margin: 5rem;
  background: #000;
  display: flex;
  position: relative;
  justify-content: center;
  border: 0.2rem dotted #9b0e0e;
  border-radius: 0.5rem;
  cursor: pointer;
  perspective: 20rem;
  transition: opacity 0.5s;
  & img {
    width: 90%;
    // height: auto;
    object-fit: cover;
    position: absolute;
    top: 2rem;
    transition: top 4s, opacity 0.2s;
    opacity: 0.4;
    pointer-events: none;
  }
  &:nth-child(odd):hover img {
    animation: oddImgAnim 4s 0.2s;
    opacity: 1;
  }
  &:nth-child(even):hover img {
    animation: oddImgAnim 4s 0.2s;
    opacity: 1;
  }
  &-img {
    animation: imgAnim 1s 1s both;
    transform-origin: top center;
    width: 100%;
    border-radius: 0.5rem;
    padding: 7rem 20rem;
    opacity: 0.9;
    display: block;
  }
}
@keyframes imgAnim {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}

@keyframes oddImgAnim {
  0% {
    transform: rotateY(0);
  }
  25% {
    transform: rotateY(-2deg) rotate(2deg);
  }
  50% {
    transform: rotateY(2deg) rotate(-2deg);
  }
  100% {
    transform: rotateY(0);
  }
}
@keyframes evenImgAnim {
  0% {
    transform: rotateY(0);
  }
  25% {
    transform: rotateY(2deg) rotate(-2deg);
  }
  50% {
    transform: rotateY(-2deg) rotate(2deg);
  }
  100% {
    transform: rotateY(0);
  }
}

.project-img-wrapper {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 0;
  background: rgba(0, 0, 0, 0.9);
  animation: imgWrapperAnim 1s forwards;
  overflow: hidden scroll;
  z-index: 100;
  scroll-behavior: smooth;
}
@keyframes imgWrapperAnim {
  0% {
    height: 0;
  }
  100% {
    height: 100%;
  }
}
.project-hide-btn {
  position: fixed;
  top: 4rem;
  right: 4rem;
  background: transparent;
  border: none;
  font-size: 2rem;
  letter-spacing: 0.1rem;
  z-index: 200;
  transform: scale(0);
  visibility: hidden;
  transition: transform 0.5s, visibility 1s;
  &:hover {
    color: #fff;
  }
  &.change {
    visibility: visible;
    transform: scale(1);
  }
}

.projects-btn {
  left: 50%;
  transform: translateX(-50%);
}

.change > .main-btn-line:nth-child(1) {
  transform: rotateZ(45deg) translateX(-0.65rem);
}
.change > .main-btn-line:nth-child(2) {
  transform: rotateZ(-45deg) translateX(0.65rem);
}
.change > .main-btn-line:nth-child(3) {
  transform: rotateZ(90deg) translateX(-0.5rem);
  border-left: 0.1rem solid #fff;
  border-right: none;
}

// section 4 --------------------------
.section-4 {
  width: 100%;
  position: relative;
}
.services-wrapper {
  width: 100%;
  flex-direction: column;
}

.service {
  width: 70%;
  margin: 2rem 0;
  border-bottom: 0.5rem solid #9b0e0e;
  &-btn {
    width: 100%;
    position: relative;
    display: block;
    height: 15rem;
    & span {
      font-family: $font2;
      font-size: 10rem;
      font-style: italic;
      font-weight: bold;
      color: #9b0e0e;
      position: absolute;
      right: 0;
      transition: right 0.5s;
      pointer-events: none;
    }
  }
  &-text {
    font-size: 5rem;
    color: #777;
    letter-spacing: 0.1rem;
    height: 0;
    visibility: hidden;
    opacity: 0;
    transition: height 0.2s, opacity 0.5s 0.5s;
    &.change {
      transition: height 0.2s, opacity 0.5s;
      opacity: 1;
      height: 15rem;
      visibility: visible;
    }
  }
}

// section 5----------------------
.section-5 {
  position: relative;
  height: 100vh;
  flex-direction: column;
}
.form-heading {
  font-family: $font2;
  font-size: 6rem;
  font-weight: 400;
  color: #9b0e0e;
  letter-spacing: 0.3rem;
  margin-bottom: 3rem;
  transition: opacity 0.3s;
}

.contact-form {
  flex-direction: column;

  &-input {
    width: 60rem;

    padding: 0.5rem;
    margin: 1rem;
    background: rgba(255, 255, 255, 0.1);
    border: 0.1rem solid #9b0e0e;
    font-size: 1.7rem;
    font-weight: bold;
    color: #aaa;
    letter-spacing: 0.1rem;
    height: 5rem;
    @at-root textarea#{&} {
      height: 20rem;
      resize: none;
    }
  }
}

.form-submit-btn {
  width: 20rem;
  height: 5rem;
  background-color: #9b0e0e;
  font-size: 1.8rem;
  color: #fff;
}

.slideshow {
  display: flex;
  width: 50rem;
  margin-top: 10rem;
  padding: 0 2rem;
  &-link {
    width: 20%;
    filter: brightness(0.5);
    transition: filter 0.5s;
    & img {
      padding: 0 1rem;
    }

    &:nth-child(1),
    &:last-child {
      transition: width 0.5s, opacity 0.5s;
    }
  }
}

.fade-out {
  opacity: 0;
  width: 0;
}

.light {
  filter: brightness(1.5);
}

// navbar ---------------------------------------
.navbar {
  position: fixed;
  top: 2.5rem;
  width: 100%;
  display: flex;
  justify-content: flex-end;
  padding-right: 3rem;
  transition: opacity 0.5s, visibility 0.5s, transform 0.5s;
  z-index: 100;
  &-link {
    font-size: 1.5rem;
    font-weight: 700;
    color: #fff;
    text-transform: uppercase;
    margin: 0 2.5rem;
    z-index: 100;
    text-shadow: 0 0.3rem 0.5rem #000;
    // opacity: 0;
    // visibility: hidden;
  }
}

.hide-navbar {
  opacity: 0;
  visibility: hidden;
  transform: translateX(10rem);
}

.menu-icon {
  position: fixed;
  top: 3.5rem;
  right: 0;
  width: 12rem;
  height: 7rem;
  // background: red;
  flex-direction: column;
  opacity: 0;
  visibility: hidden;
  z-index: 101;
  transition: opacity 0.5s, visibility 0.5s;
  transform: translate(-50%, -50%);
  cursor: pointer;
  &-line {
    width: 4rem;
    height: 0.1rem;
    background: #fff;
    margin: 0.5rem 0;
    box-shadow: 0 0.3rem 0.5rem #000;
  }
}
.show-menu-icon {
  opacity: 1;
  visibility: visible;
}

// ---------------------
.progress-bar {
  position: fixed;
  background: #fff;
  width: 4rem;
  height: 4rem;
  bottom: 8rem;
  right: 8rem;
  border-radius: 50%;
  z-index: 200;
  overflow: hidden;
  transform: translate(-50%, -50%);
  &-circle {
    border-radius: 50%;
    width: 7.8rem;
    height: 7.8rem;
    background: #000;
    color: #fff;
    font-size: 4rem;
    // display: none;
    z-index: 200;
    transition: transform 0.5s;
    pointer-events: none;
  }
  // &:hover .half-circle:nth-child(1) {
  //   transform: rotate(180deg);
  //   transition: transform 1s linear;
  // }
  // &:hover .half-circle:nth-child(2) {
  //   transition: transform 2s linear;
  //   transform: rotate(360deg);
  // }
  // &:hover .half-circle-top {
  //   transition: opacity 2s 1s linear;
  //   opacity: 0;
  // }
}
.half-circle,
.half-circle-top {
  position: absolute;
  top: 0;
  left: 0;
  width: 55%;
  height: 105%;
  transform-origin: right center;
  pointer-events: none;
}
.half-circle:nth-child(1) {
  background: #9b0e0e;
}
.half-circle:nth-child(2) {
  background: #9b0e0e;
}
.half-circle-top {
  background: #fff;
}

// ---------------------
@media (max-width: 1500px) {
  .circle {
    width: 15rem;
    height: 15rem;
  }
  .main-circle {
    width: 45rem;
    height: 45rem;
    &:hover {
      width: 55rem;
      height: 55rem;
      & img {
        width: 160%;
        height: 160%;
      }
    }
  }
  .about-me-text {
    font-size: 11rem;
  }
  .section-4 {
    margin-bottom: 10rem;
  }
  .service {
    &-btn span {
      font-size: 9rem;
    }
    &-text {
      font-size: 4rem;
    }
  }
}
@media (max-width: 1350px) {
  .main-btn {
    width: 12.5rem;
    height: 12.5rem;
  }
  .circle {
    display: none;
  }
  .progress {
    &-bar {
      right: 1rem;
      bottom: 0;
    }
  }
  .about-me-text span {
    font-size: 10rem;
  }
}
@media (max-width: 1150px) {
  html {
    font-size: 55%;
  }
  .mouse {
    &-circle {
      &-dot {
        display: none;
      }
    }
  }
  .navbar {
    &-link {
      margin: 0 2rem;
    }
  }
  .main {
    &-circle {
      width: 40rem;
      height: 40rem;
      &:hover {
        width: 45rem;
        height: 45rem;
      }
    }
  }
  .about-me-text span {
    font-size: 7.5rem;
  }
  .project-img {
    padding: 7rem 11rem;
  }
  .project-hide-btn {
    right: 2rem;
  }
  .service {
    &-brn {
      height: 12rem;
    }
    &-btn span {
      font-size: 8rem;
    }
    &-text {
      font-size: 3.5rem;
    }
  }
  .slideshow {
    margin: 5rem 0;
  }
}
@media (max-width: 900px) {
  .menu-icon {
    right: -4px;
  }
  .navbar-link {
    font-size: 1.4rem;
    margin: 0 1rem;
  }
  .feature-text {
    &-1 {
      margin: 0 auto 0 0.5rem;
    }
    &-2 {
      margin: 0 5rem 0 auto;
    }
  }
  .section-heading {
    left: 5rem;
  }
  .about-me-text span {
    color: #ccc;
    font-size: 5.5rem;
  }

  .service {
    &-btn {
      height: 9rem;
    }
    &-btn span {
      font-size: 6rem;
    }

    &-text {
      font-size: 3.2rem;
    }
  }
}

@media (max-width: 700px) {
  .contact-form-input {
    width: 50rem;
  }
}
@media (max-width: 550px) {
  html {
    font-size: 40%;
  }
  .main {
    &-circle img {
      width: 180%;
      height: 180%;
    }
  }
  .service {
    &-btn span {
      font-size: 4.5rem;
    }
    &-text {
      font-size: 3rem;
    }
  }
  .contact-form-input {
    width: 40rem;
  }
  .slideshow {
    width: 45rem;
    &-link img {
      padding: 0 1.1rem;
    }
  }
}

@media (max-width: 400px) {
  html {
    font-size: 35%;
  }
  .logo {
    font-size: 1.7rem;
    top: 13rem;
    left: 50%;
    transform: translateX(-50%);
  }
  .navbar {
    justify-content: center;
    padding: 0;
  }
  .main {
    &-circle {
      width: 38rem;
      &:hover {
        width: 42rem;
      }
      & img {
        width: 200%;
        height: 200%;
      }
    }
  }
  .form-heading {
    font-size: 5rem;
  }
}
// form ---------------
.message {
  font-size: 1.2rem;
  font-weight: 700;
  color: #f55e5e;
  text-transform: uppercase;
  letter-spacing: 0.1rem;
  margin: 0 auto 1rem 0;
  visibility: hidden;
  opacity: 0;
  transition: opacity 0.3s;
}
.error.message {
  visibility: visible;
  opacity: 1;
}
